import React from "react";
import ReactDOM from "react-dom";
import App from "./App";

// import drizzle functions and contract artifact
import { Drizzle, generateStore } from "drizzle";

import ProgPayETH from "./contracts/ProgPayETH.json";

import { Route, Link, BrowserRouter as Router } from 'react-router-dom'

// let drizzle know what contracts we want
const options = { contracts: [ProgPayETH] };

// setup the drizzle store and drizzle
const drizzleStore = generateStore(options);
const drizzle = new Drizzle(options, drizzleStore);// pass in the drizzle instance

const routing = (
  <Router>
    <div>
      <Route path="/:address" render={props => (<App {...props} drizzle={drizzle}/>)} />
    </div>
  </Router>
)
ReactDOM.render(routing, document.getElementById("root"));
