export default class MyDrizzleApp extends React.Component {

  constructor(props){
    super(props);
    this.state = {payeeIndex:null, payerIndex:null};
  }

 componentDidMount() {

    //access drizzle props within componentDidMount
    const { drizzle} = this.props;

    const contract = drizzle.contracts.ProgPayETH;

    const payeeIndex = contract.methods["payee"].cacheCall();
    const payerIndex = contract.methods["payer"].cacheCall();

    this.setState({payeeIndex, payerIndex});


 }
render() {
  const ProgPayETH = this.props.drizzleState.contracts;

  const payee = ProgPayETH.payee[this.state.payeeIndex];
