      <div>
      <p>This Contract's Payer is {payer && payer.value} and the Payee is {payee && payee.value}.</p>
      <p> The total value of this contract is {contractValue && contractValue.value}.</p>

      </div>
    );
  }
}

export default MyDrizzleApp;
