import React from "react";
import { DrizzleContext, DrizzleProvider } from "drizzle-react";
import { Drizzle, generateStore } from "drizzle";

import ProgPayETH from "./contracts/ProgPayETH.json";
import MyDrizzleApp from "./MyDrizzleApp"; // Check out drizzle's react components at @drizzle/react-components


const drizzleOptions = {
  contracts: [ProgPayETH],
};

const drizzleStore = generateStore(drizzleOptions);

const drizzle = new Drizzle(drizzleOptions, drizzleStore);

const App = () => {
  return (
    <DrizzleContext.Provider drizzle={drizzle}>
      <DrizzleContext.Consumer>
        {drizzleContext => {
          const {drizzle, drizzleState, initialized} = drizzleContext;

          if(!initialized) {
            return "Loading..."
          }

          return (
            <MyDrizzleApp drizzle={drizzle} drizzleState={drizzleState} />
            )
          }}
      </DrizzleContext.Consumer>
    </DrizzleContext.Provider>
  );
}

export default App;
