import React, { Component } from 'react';
import GetContractInfo from './GetContractInfo';
import abi from './abi.js';

//import Web3 from 'web3';
//const web3 = new Web3(Web3.givenProvider || "http://localhost:8545");



class App extends Component {
  state = { loading: true, drizzleState: null, cAddress:null };

  componentDidMount() {
    const { drizzle } = this.props;
    const address = this.props.match.params.address;
    const web3 = this.props.drizzle;

    if (web3){
    let isETHAddress = web3.utils.isAddress(address)?address:false;
} 




    // subscribe to changes in the store
    this.unsubscribe = drizzle.store.subscribe(() => {

      // every time the store updates, grab the state from drizzle
      const drizzleState = drizzle.store.getState();

      // check to see if it's ready, if so, update local component state
      if (drizzleState.drizzleStatus.initialized) {
        this.setState({ loading: false, drizzleState , cAddress:isETHAddress});
      }
    });

    if (isETHAddress){
    var contractConfig = {
      contractName: "DynamicProgPayETH",
      web3Contract: new web3.eth.Contract(abi,isETHAddress)
    }
    //drizzle.store.dispatch({type: 'ADD_CONTRACT', contractConfig});
    drizzle.addContract(contractConfig);
    }
    }

  compomentWillUnmount() {
    this.unsubscribe();
  }



  render() {
    console.log(this.props.drizzle.web3.utils && this.props.drizzle.web3.utils.isAddress("abs"));
    if (this.state.loading) return "Loading Drizzle...";
    if (!this.state.cAddress) return "Not a valid address...";
    return(


        <GetContractInfo
        drizzle={this.props.drizzle}
        drizzleState={this.state.drizzleState}
        />

      )
  }
}export default App;
