import React from 'react';

import bytecode from './bytecode.js';

class Main extends React.Component {
  constructor(props){
    super(props);
    this.state={};
  }

  handleDeployContract(){
    const { drizzle } = this.props;
    console.log(drizzle);
    const { web3 } = drizzle;
    console.log(web3);
    const progPayETHContract = new web3.eth.Contract(this.props.options.contracts[0].abi, {data:bytecode});
    console.log(progPayETHContract);
    let args = ["0xf3860788D1597cecF938424bAABe976FaC87dC26","0x5462deE16b3f4D240EBBE52C9C4214c912ccDda4","100000000000000000","3","300"];
    let defaultAcct;
    web3.eth.getAccounts().then((e) => {console.log(e[0])});


    //web3.eth.getAccounts().then(e => {let firstAcc=e[0]; console.log(firstAcc)});
    progPayETHContract.deploy({arguments:args}).send({
    from: defaultAcct,
    gas: 1500000,
    gasPrice: '30000000000000'
})
.then(function(newContractInstance){
    console.log(newContractInstance.options.address) // instance with the new contract address
});;


  }


  render(){
    return (
      <div>
      <p>Main page will be here</p>
      <button onClick={this.handleDeployContract.bind(this)}>TestW3</button>

      </div>
    )
  }
}

export default Main;
