import React from "react";

import Web3 from 'web3';
const web3 = new Web3(Web3.givenProvider || "http://localhost:8545");

class DissolveFunctions extends React.Component {

  constructor(props){
    super(props);
    this.state ={};

  }


  componentDidMount() {

     const { drizzle } = this.props;
     const contract = drizzle.contracts.ProgPayETH;

     const payerDissolveIndex = contract.methods['payerWantsOut'].cacheCall();
     const payeeDissolveIndex = contract.methods['payeeWantsOut'].cacheCall();

/*
       const paymentIndex = contract.methods["paymentNumberToValue"].cacheCall(i+1);
       const requestedIndex = contract.methods["paymentNumberToRequested"].cacheCall(i+1);
       const approvedIndex = contract.methods["paymentNumberToApproved"].cacheCall(i+1);
*/

    this.setState({payerDissolveIndex, payeeDissolveIndex});
  }




  render(){

    const { drizzle, drizzleState } = this.props;
    const contract = drizzleState.contracts.ProgPayETH;

    const payerDissolve = contract.payerWantsOut[this.state.payerDissolveIndex];
    const payeeDissolve = contract.payeeWantsOut[this.state.payeeDissolveIndex];

    console.log(payerDissolve && "payer "+ payerDissolve.value);
    console.log(payeeDissolve && "payee "+payeeDissolve.value);



    return (

      <div>
      <h4>Contract Dissolution Status</h4>
      <p>Payer {payerDissolve && payerDissolve.value===true?"wants":"does not want"} to dissolve.</p>
      <p>Payee {payeeDissolve && payeeDissolve.value===true?"wants":"does not want"} to dissolve.</p>
      </div>
    );
}
}



export default DissolveFunctions;
